import React from 'react'
import { Pagination } from 'react-bootstrap'
import { LinkContainer } from 'react-router-bootstrap'

function Paginate({page, pages, keyword = '', isAdmin = false}) {

    if(keyword){
        keyword = keyword.split('?keyword=')[1].split('&')[0]
    }

  return (
    pages > 1 && (
      <Pagination>
        {[...Array(pages).keys()].map((x) => {
          const pageNumber = x + 1;
          const isActive = pageNumber === page;
          console.log(`pageNumber: ${pageNumber}, isActive: ${isActive}`);
          return (
            <LinkContainer key={pageNumber} to={{
              pathname: "/",
              search: `?keyword=${keyword}&page=${pageNumber}`,
            }}>
              <Pagination.Item active={isActive}>{pageNumber}</Pagination.Item>
            </LinkContainer>
          );
        })}
      </Pagination>
    )
  );
}

export default Paginate
